mapper.select:
SELECT m.messageId, m.sessionId, m.fromId, u.username as fromName, m.content, m.createTime FROM message_session_user msu JOIN message m ON m.sessionId = msu.sessionId JOIN user u ON m.fromId = u.userId WHERE msu.userId = #{userId}
AND m.content like concat('%', #{query}, '%')
ORDER BY m.createTime DESC LIMIT 50
