mapper.select:
select u.userId as friendId, u.username as friendName from user u join message_session_user msu
on u.userId = msu.userId
where msu.sessionId = #{sessionId} and msu.userId != #{userId}

select u.userId as friendId, u.username as friendName from user u join message_session_user msu
on u.userId = msu.userId
where msu.sessionId = #{sessionId}
